&lt;html&gt;&lt;head&gt;&lt;/head&gt;&lt;body&gt;&lt;h3 id=&#34;Week-1-of-the-making-of-ScribeHacks&#34;&gt;&lt;a href=&#34;#Week-1-of-the-making-of-ScribeHacks&#34; class=&#34;headerlink&#34; title=&#34;Week 1 of the making of ScribeHacks&#34;&gt;&lt;/a&gt;Week 1 of the making of ScribeHacks&lt;/h3&gt;&lt;script&gt;
       document.querySelectorAll(&#39;.github-emoji&#39;)
         .forEach(el =&gt; {
           if (!el.dataset.src) { return; }
           const img = document.createElement(&#39;img&#39;);
           img.style = &#39;display:none !important;&#39;;
           img.src = el.dataset.src;
           img.addEventListener(&#39;error&#39;, () =&gt; {
             img.remove();
             el.style.color = &#39;inherit&#39;;
             el.style.backgroundImage = &#39;none&#39;;
             el.style.background = &#39;none&#39;;
           });
           img.addEventListener(&#39;load&#39;, () =&gt; {
             img.remove();
           });
           document.body.appendChild(img);
         });
